import { useState } from "react";
import SingleCat from "./SingleCat";
import AddCatForm from "./AddCatForm";

const cats = [
  {
      id:1,
    name: 'Cheetah',
    latinName: 'Acinonyx jubatus' ,
    image:'data:image/jpeg;base64,'
  },

  { 
      id:2,
      name: 'Cougar',
      latinName: 'Puma concolor',
      image:'data:image/jpeg;base64,' 
  },
  {
      id:3,
       name: 'Jaguar',
      latinName: 'Panthera onca',
      image:'data:image/jpeg;base64,' 
  },
  { 
      id:4,
      name: 'Leopard',
      latinName: 'Panthera pardus',
      image:'data:image/jpeg;base64,' 
  },
  { 
      id:5,
      name: 'Lion',
      latinName: 'Panthera leo' ,
      image:'data:image/jpeg;base64,'
  },
  { 
      id:6,
      name: 'Snow leopard',
      latinName: 'Panthera uncia',
      image:'data:image/jpeg;base64,'
   },
  { 
      id:7,
      name: 'Tiger',
      latinName: 'Panthera tigris',
      image:'data:image/jpeg;base64,'
   },
];


function BigCats(){

  const[bigcats,setBigcats]=useState(cats);

  const handleDeleteCat=(id)=>{
    let newCats=bigcats.filter(cat=>cat.id!=id);
    setBigcats(newCats);
  }


  const handleAddCat = (newCat) => {
    // newMovie.id = currentMovies.length + 1; // unreliable but succinct
    // setCurrentMovies([...currentMovies, newMovie])
    let newCats=[...bigcats,{...newCat,id:bigcats.length+1}];
    setBigcats(newCats);
    }
    

const catList = bigcats.map(cat => (
 <SingleCat key={cat.id} id={cat.id} name={cat.name} latinName={cat.latinName} image={cat.image} onDeleteCat={handleDeleteCat}/>
   
  
    ))

const handleReverseCats = ()=>{
  let newcats=[...bigcats];
  newcats.reverse();
  setBigcats(newcats);
}

const handleSortCats = ()=>{
  let newcats=[...bigcats];
  newcats.sort((a, b) => a.name.localeCompare(b.name));
  setBigcats(newcats);
}

const handlePanthera=()=>{
      let filterPanthera=bigcats.filter(cat=>cat.latinName.startsWith('Panthera'));
      setBigcats(filterPanthera);
}

const handleReset=()=>{
    setBigcats(cats);
}
return (
    <div className="BigCats componentBox">
      <ul>
        {/* display list of list items */}
        {/* iterate over each cat, print the attributes in a catList */}
        {catList}
      </ul>
      <button onClick={handleSortCats}>Sort Cats</button>
      <button onClick={handleReverseCats}>Reverse Cats</button>
      <button onClick={handlePanthera}>Panthera Cats</button>
      <button onClick={handleReset}>Reset</button>
      <AddCatForm onAddCat={handleAddCat}/>
    </div>
  );
}
export default BigCats;